import React, {Component} from 'react';

class Todolist extends Component {
    state = {
        todo: [
            {id: 0, msg:"coucou"}],
        newtodo : '',
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const id = new Date().getTime();
        const valueinput = this.state.newtodo
        const newtodo = {id : id, valueinput: valueinput}
        const todo = this.state.todo.slice();
        todo.push(valueinput)
    }

    handleChange = (event) => {
        const value = event.currentTarget.value
        this.setState({ newtodo : value});
    }

    handleDelete = (event) => {
        const todo = this.state.todo.slice();
        const index = todo.findIndex((todo) => todo.id === id
        )
        todo.splice(index, 1);
        this.setState({todo : todo});
    }

    render() {
        const elements = this.state.Todo.map(Todo => ( 
        <li>{todo.todo}</li>, <button onClick={ () =>this.handleDelete(todo.id)}>X</button> ));
        return (
            <div>
<ul>
    {elements}
</ul>
<form onSubmit={this.handleSubmit}>
<input value={this.state.newtodo} onChange={this.handleChange} type="text" placeholder="Votre message..." />
<button onClick={() => this.handleClick()}>Add</button>
</form>
</div>
        )
    }
}

export default Todolist
