import React , {Component} from 'react'

class Todolist extends Component {
    constructor() {
        super();
        this.state = {
            userInput: '',
            items: []
        }
    }

type(event) {
    this.setState({
        userInput: event.target.value
    } , () => console.log(this.state.userInput))
}   
addTodo(event) {
    event.preventDefault();
    this.setState({
        items: [...this.state.items, this.state.userInput],
        userInput: ''
    })
}

deletTodo(event) {
    event.preventDefault();
    const tab = this.state.items;
    const index = tab.indexOf(event.target.value)
    tab.splice(index, 1)
    this.setState({
        items: tab
    })
}


AfficheTodo(event) {
    return this.state.items.map((item) => {
        return (
            <div key={item}>
                {item}  | <button onClick={this.deletTodo.bind(this)}>Supprimer</button>
            </div>
        )
    })
}

    render () {
        return (
            <div>
                <h1 align="center">Ma Tout doux liste</h1>
                <form className="form-horizontal">
                    <input 
                    value={this.state.userInput}
                    type='texte' 
                    placeholder='Choses a faire'
                    onChange={this.type.bind(this)}
                    className="form-control col-lg-6"
                    />
                    <button 
                    onClick={this.addTodo.bind(this)}
                    >Send</button>
                </form>
                <div>
                    {this.AfficheTodo()}
                </div>
            </div>
        )
    }
}

export default Todolist;
