import React, {Component} from 'react';

class Todolist extends Component {
    state = {
        clients: [
            {id: 0, msg:"coucou"}
        ],
        newtodo : ''
    }

    render() {
        const elements = this.state.elements.map(Todo => ( 
        <li>{this.state.todo.msg}</li>, <button onClick={() => this.handleDelete(todo.id)}>X</button> ));
        return (
            <div>
<ul>
    {this.state.elements.map(element => {
        <li>
            {elements.msg} <button onClick={() => this.handleDelete}> X</button>
        </li>
    })}
</ul>
<form onSubmit={this.handleSubmit}>
<input value={this.state.newtodo} onChange={this.handleChange} type="text" placeholder="Votre message..." />
<button onClick={() => this.handleClick()}>Add</button>
</form>
</div>
        )
    }
}

export default Todolist
