import React, {Component} from 'react';

class Todolist extends Component {
    state = {
        clients : [
            {id: 0, msg:"coucou"}
        ],
        newtodo : ''
    }

    render() {
        return (
            <div>
                <ul> 
                    {this.state.clients.map(client => (
                    <li>
                        {clients.msg} <button>X</button>
                    </li> 
                ))}
                </ul>
                <form>
                    <input type='text' placeholder="Votre message..."/>
                </form>
            </div>
        )
    }
}

export default Todolist
