import React, { Component } from 'react';

class TodoList extends Component {
    constructor() {
        super();
        this.state = {
            userInput: '',
            items: []
        };
    }

    onChange(event) {
        this.setState({
            userInput: event.target.value
        });
    }

    addTodo(event) {
        event.preventDefault();
        this.setState({
            userInput: '',
            items: [...this.state.items, this.state.userInput]
        });
    }

    deleteTodo(item) {
        // no event 
        // pass the item to indexOf
        const array = this.state.items;
        const index = array.indexOf(item);
        array.splice(index, 1);
        this.setState({
            items: array
        });
    }

    renderTodos() {
        return this.state.items.map((item) => {
            return (
                <div className="list-group-item" key={item}>
                    {item} | <button onClick={this.deleteTodo.bind(this, item)}>X</button>
                </div>    
            );
        });
    }

    render () {
        return (
            <div>
                <h1 align="center">Ma Tout doux liste</h1>
                <div className="row">
                <form className="form-horizontal">

                    <input 
                    value={this.state.userInput}
                    type='texte' 
                    placeholder='Choses a faire'
                    onChange={this.type.bind(this)}
                    className="form-control"
                    />
                    <button 
                    onClick={this.addTodo.bind(this)}
                    className="btn-cool"
                    >
                    Send</button>
                </form>
                </div>
                <div className="card">
                <div className="AfficheTodo list-group">
                    {this.AfficheTodo()}
                </div>
            </div>
            </div>
        )
    }
}

export default Todolist;
